import React from 'react';
import { useNavigate } from 'react-router-dom'; // use this for navigation
import '../styles/jobs.css';

const SearchJobs = () => {
  const navigate = useNavigate();

  const handlePostJobClick = () => {
    navigate('/post-job'); // Make sure this matches your route
  };

  const jobs = [
    { title: 'Frontend Developer', location: 'Remote', description: 'React developer needed.' },
    { title: 'Backend Developer', location: 'Bangalore', description: 'Node.js + MongoDB role.' }
  ];

  return (
    <div className="container">
      <div className="top-bar">
        <h2>Available Jobs</h2>
        <button className="post-btn" onClick={handlePostJobClick}>+ Post a Job</button>
      </div>

      {jobs.map((job, index) => (
        <div key={index} className="job-card">
          <h3>{job.title}</h3>
          <p><strong>Location:</strong> {job.location}</p>
          <p>{job.description}</p>
        </div>
      ))}
    </div>
  );
};

export default SearchJobs;
