import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/jobs.css';

const SearchJobs = () => {
  const navigate = useNavigate();
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/jobs/list');
        setJobs(res.data);
      } catch (err) {
        console.error('Failed to fetch jobs:', err);
      }
    };

    fetchJobs();
  }, []);

  const handlePostJobClick = () => {
    navigate('/post-job');
  };

  return (
    <div className="container">
      <div className="top-bar">
        <h2>Available Jobs</h2>
        <button className="post-btn" onClick={handlePostJobClick}>+ Post a Job</button>
      </div>

      {jobs.map((job, index) => (
        <div key={index} className="job-card">
          <h3>{job.title}</h3>
          <p><strong>Location:</strong> {job.location}</p>
          <p>{job.description}</p>
        </div>
      ))}

{/* ✅ Add this below job cards */}
<button className="back-btn" onClick={() => navigate(-1)}>← Previous Page</button>

    </div>
  );
};

export default SearchJobs;
